$(document).ready(function() {
  $(".tab-content-1").hide();
  $("ul.tab-menu li:first").addClass("active").show();
  $(".tab-content-1:first").show();
  $("ul.tab-menu li").click(function() {
    $("ul.tab-menu li").removeClass("active");
    $(this).addClass("active");
    $(".tab-content-1").hide();
    var e = $(this).find("a").attr("href");
    $(e).fadeIn();
    return !1;
  });
});